import fovusLogo from '../assets/FovusLogo.jpg';
import React ,{useState} from 'react';

const S3_BUCKET ='fovuso';
const REGION ='us-west-1';
const ACCESS_KEY = 'AKIAZKFTDZ5EYDXCCAEU';
const SECRET_ACCESS_KEY = '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';

AWS.config.update({
    accessKeyId: ACCESS_KEY,
    secretAccessKey: SECRET_ACCESS_KEY,
    region: REGION,
  });

const myBucket = new AWS.S3();

export default function Form() {

    const [progress , setProgress] = useState(0);
    const [selectedFile, setSelectedFile] = useState(null);
    const handleFileInput = (e) => {
        setSelectedFile(e.target.files[0]);
        console.log("eloasd", e.target.files[0]);
    }

    const uploadTaskFile = async (event) => {
        try {
            setUploading(true);

            // Intitiate the upload by fetching the S3 presigned URL from the API
            const response = await api.post("task/presignedURL", {
                jobId,
                taskLocale: task.locale,
                filename: event.target.files[0].name
            });

            // Upload the file to S3
            const formData = new FormData();
            formData.append("bucket", response.data.fields.bucket);
            formData.append("X-Amz-Algorithm", response.data.fields["X-Amz-Algorithm"]);
            formData.append("X-Amz-Credential", response.data.fields["X-Amz-Credential"]);
            formData.append("X-Amz-Date", response.data.fields["X-Amz-Date"]);
            formData.append("key", response.data.fields.key);
            formData.append("Policy", response.data.fields.Policy);
            formData.append("X-Amz-Signature", response.data.fields["X-Amz-Signature"]);
            formData.append("file", event.target.files[0]);

            const resS3 = await api.post(response.data.url, formData, {
                timeout: 60 * 60 * 1000,
                headers: {
                    Authorization: null,
                    "Content-Type": "multipart/form-data"
                }
            });

            // Inform the API that the file has been uploaded
            const res = await api.post("task/uploadComplete", {
                jobId,
                taskLocale: task.locale,
                filename: event.target.files[0].name,
                tempS3Key: response.data.fields.key
            });

            await getJobData();

            enqueueSnackbar(res.data.message, { variant: "success", autoHideDuration: 1500 });
        }
        catch (error) {
            console.log(error);
            if (error.response?.status === 400) {
                enqueueSnackbar(error.response.data.message, { variant: "error", autoHideDuration: 3500 });
            }
            else {
                enqueueSnackbar("Something went wrong", { variant: "error", autoHideDuration: 2000 });
            }
        }
        finally {
            // Reset the file input value
            inputRef.current.value = "";
            setUploading(false);
        }
    };

    return (
        <div className='grid grid-cols-1 sm:grid-cols-2 h-screen w-full'>
            <div className='hidden sm:block'>
                <img className='w-full h-full object-cover' src={fovusLogo} alt=""/>
            </div>

            <div className='bg-gray-300 flex flex-col justify-center'>
                <form className='max-w-[500px] w-full mx-auto bg-gray-100 p-8 px-8 rounded-lg' >
                    <h2 className='text-4xl dark:text-black font-bold text-center'>Fovus Challenge Form</h2>

                    <div className='flex flex-col text-gray-700 py-2'>
                        <label>Text Input</label>
                        <input className='rouded-lg bg-white mt-2 p-2 focus-within:border-black' type="text" placeholder="Enter text Input" required/>
                    </div>

                    <div className='flex flex-col text-gray-700 py-2'>
                        <label>File Input</label>
                        <input type='file' name='input_file' id='input_file' onChange={handleFileInput} required/>
                    </div>

                    <div className='flex justify-between text-gray-900 py-2'>
                        <p>Please attach a File</p>
                    </div>
                    <button onClick={() => uploadFile(selectedFile)} className='w-full my-5 py-2 bg-gray-700 shadow-lg rounded-sm text-white shadow-gray-700/50 hover:shadow-gray-700/30 font-semibold'>Submit</button>
                </form>
            </div>
        </div>
    )
}
